select
id, titile, user_id, section_id, reply_num, view_num, approve_num, collect_num, content, post_time, update_time,
image, video, type, can_read_users
from bbs.t_post where section_id=#{id}
limit #{start}, #{num}

select
id, titile, user_id, section_id, reply_num, view_num, approve_num, collect_num, content, post_time, update_time,
image, video, type, can_read_users
from bbs.t_post where section_id=#{id}
limit #{start}, #{num}

select
id, titile, user_id, section_id, reply_num, view_num, approve_num, collect_num, content, post_time, update_time,
image, video, type, can_read_users
from bbs.t_post where section_id=#{type}
limit #{start}, #{num}

select
id, titile, user_id, section_id, reply_num, view_num, approve_num, collect_num, content, post_time, update_time,
image, video, type, can_read_users
from bbs.t_post


        and id = #{id}


        and titile = #{titile}


        and user_id = #{userId}


        and section_id = #{sectionId}


        and reply_num = #{replyNum}


        and view_num = #{viewNum}


        and approve_num = #{approveNum}


        and collect_num = #{collectNum}


        and content = #{content}


        and post_time = #{postTime}


        and update_time = #{updateTime}


        and image = #{image}


        and video = #{video}


        and type = #{type}


        and can_read_users = #{canReadUsers}
